	{
		if ($this->collMataKuliahJurusans === null) {
			$this->initMataKuliahJurusans();
		}
		if (!in_array($l, $this->collMataKuliahJurusans, true)) { // only add it if the **same** object is not already associated
			array_push($this->collMataKuliahJurusans, $l);
			$l->setJurusan($this);
		}
	}


	/**
