	 *
	 * This method is protected by default in order to keep the public
	 * api reasonable.  You can provide public methods for those you
	 * actually need in Jurusan.
	 */
	public function getMataKuliahJurusansJoinMataKuliah($criteria = null, $con = null, $join_behavior = Criteria::LEFT_JOIN)
	{
		if ($criteria === null) {
			$criteria = new Criteria(JurusanPeer::DATABASE_NAME);
		}
		elseif ($criteria instanceof Criteria)
		{
			$criteria = clone $criteria;
		}

		if ($this->collMataKuliahJurusans === null) {
			if ($this->isNew()) {
				$this->collMataKuliahJurusans = array();
			} else {

				$criteria->add(MataKuliahJurusanPeer::KODE_JUR, $this->kode_jur);

				$this->collMataKuliahJurusans = MataKuliahJurusanPeer::doSelectJoinMataKuliah($criteria, $con, $join_behavior);
			}
		} else {
			// the following code is to determine if a new query is
			// called for.  If the criteria is the same as the last
			// one, just return the collection.

			$criteria->add(MataKuliahJurusanPeer::KODE_JUR, $this->kode_jur);

			if (!isset($this->lastMataKuliahJurusanCriteria) || !$this->lastMataKuliahJurusanCriteria->equals($criteria)) {
				$this->collMataKuliahJurusans = MataKuliahJurusanPeer::doSelectJoinMataKuliah($criteria, $con, $join_behavior);
			}
		}
		$this->lastMataKuliahJurusanCriteria = $criteria;

		return $this->collMataKuliahJurusans;
	}

	/**
	 * Clears out the collKaryawans collection (array).
	 *
	 * This does not modify the database; however, it will remove any associated objects, causing
	 * them to be refetched by subsequent calls to accessor method.
	 *
	 * @return     void
	 * @see        addKaryawans()
	 */
	public function clearKaryawans()
	{
		$this->collKaryawans = null; // important to set this to NULL since that means it is uninitialized
	}

	/**
	 * Initializes the collKaryawans collection (array).
	 *
	 * By default this just sets the collKaryawans collection to an empty array (like clearcollKaryawans());
	 * however, you may wish to override this method in your stub class to provide setting appropriate
	 * to your application -- for example, setting the initial array to the values stored in database.
	 *
	 * @return     void
	 */
	public function initKaryawans()
	{
		$this->collKaryawans = array();
	}

	/**
	 * Gets an array of Karyawan objects which contain a foreign key that references this object.
	 *
	 * If this collection has already been initialized with an identical Criteria, it returns the collection.
	 * Otherwise if this Jurusan has previously been saved, it will retrieve
	 * related Karyawans from storage. If this Jurusan is new, it will return
	 * an empty collection or the current collection, the criteria is ignored on a new object.
	 *
	 * @param      PropelPDO $con
	 * @param      Criteria $criteria
	 * @return     array Karyawan[]
	 * @throws     PropelException
	 */
	public function getKaryawans($criteria = null, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(JurusanPeer::DATABASE_NAME);
		}
		elseif ($criteria instanceof Criteria)
		{
			$criteria = clone $criteria;
		}

		if ($this->collKaryawans === null) {
			if ($this->isNew()) {
			   $this->collKaryawans = array();
			} else {

				$criteria->add(KaryawanPeer::KODE_JUR, $this->kode_jur);

				KaryawanPeer::addSelectColumns($criteria);
				$this->collKaryawans = KaryawanPeer::doSelect($criteria, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return the collection.


				$criteria->add(KaryawanPeer::KODE_JUR, $this->kode_jur);

				KaryawanPeer::addSelectColumns($criteria);
				if (!isset($this->lastKaryawanCriteria) || !$this->lastKaryawanCriteria->equals($criteria)) {
					$this->collKaryawans = KaryawanPeer::doSelect($criteria, $con);
				}
			}
		}
		$this->lastKaryawanCriteria = $criteria;
		return $this->collKaryawans;
	}

	/**
	 * Returns the number of related Karyawan objects.
	 *
	 * @param      Criteria $criteria
	 * @param      boolean $distinct
	 * @param      PropelPDO $con
	 * @return     int Count of related Karyawan objects.
	 * @throws     PropelException
	 */
	public function countKaryawans(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(JurusanPeer::DATABASE_NAME);
		} else {
			$criteria = clone $criteria;
		}

		if ($distinct) {
			$criteria->setDistinct();
		}

		$count = null;

		if ($this->collKaryawans === null) {
			if ($this->isNew()) {
				$count = 0;
			} else {

				$criteria->add(KaryawanPeer::KODE_JUR, $this->kode_jur);

				$count = KaryawanPeer::doCount($criteria, false, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return count of the collection.


				$criteria->add(KaryawanPeer::KODE_JUR, $this->kode_jur);

				if (!isset($this->lastKaryawanCriteria) || !$this->lastKaryawanCriteria->equals($criteria)) {
					$count = KaryawanPeer::doCount($criteria, false, $con);
				} else {
					$count = count($this->collKaryawans);
				}
			} else {
				$count = count($this->collKaryawans);
			}
		}
		return $count;
	}

	/**
	 * Method called to associate a Karyawan object to this object
	 * through the Karyawan foreign key attribute.
	 *
	 * @param      Karyawan $l Karyawan
	 * @return     void
	 * @throws     PropelException
	 */
	public function addKaryawan(Karyawan $l)
	{
		if ($this->collKaryawans === null) {
			$this->initKaryawans();
		}
		if (!in_array($l, $this->collKaryawans, true)) { // only add it if the **same** object is not already associated
			array_push($this->collKaryawans, $l);
			$l->setJurusan($this);
		}
	}

	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collAdmins) {
				foreach ((array) $this->collAdmins as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collDosens) {
				foreach ((array) $this->collDosens as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collKelasJurusans) {
				foreach ((array) $this->collKelasJurusans as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collMataKuliahJurusans) {
				foreach ((array) $this->collMataKuliahJurusans as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collKaryawans) {
				foreach ((array) $this->collKaryawans as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collAdmins = null;
		$this->collDosens = null;
		$this->collKelasJurusans = null;
		$this->collMataKuliahJurusans = null;
		$this->collKaryawans = null;
	}

	// symfony_behaviors behavior
	
	/**
	 * Calls methods defined via {@link sfMixer}.
	 */
	public function __call($method, $arguments)
	{
	  if (!$callable = sfMixer::getCallable('BaseJurusan:'.$method))
	  {
	    throw new sfException(sprintf('Call to undefined method BaseJurusan::%s', $method));
	  }
	
	  array_unshift($arguments, $this);
	
	  return call_user_func_array($callable, $arguments);
	}

