	 * @throws     PropelException
	 */
	public function countDosenMataKuliahs(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(MataKuliahPeer::DATABASE_NAME);
		} else {
			$criteria = clone $criteria;
		}

		if ($distinct) {
			$criteria->setDistinct();
		}

		$count = null;

		if ($this->collDosenMataKuliahs === null) {
			if ($this->isNew()) {
				$count = 0;
			} else {

				$criteria->add(DosenMataKuliahPeer::KODE_MK, $this->kode_mk);

				$count = DosenMataKuliahPeer::doCount($criteria, false, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return count of the collection.


				$criteria->add(DosenMataKuliahPeer::KODE_MK, $this->kode_mk);

				if (!isset($this->lastDosenMataKuliahCriteria) || !$this->lastDosenMataKuliahCriteria->equals($criteria)) {
					$count = DosenMataKuliahPeer::doCount($criteria, false, $con);
				} else {
					$count = count($this->collDosenMataKuliahs);
				}
			} else {
				$count = count($this->collDosenMataKuliahs);
			}
		}
		return $count;
	}

	/**
