	public function addDosenMataKuliah(DosenMataKuliah $l)
	{
		if ($this->collDosenMataKuliahs === null) {
			$this->initDosenMataKuliahs();
		}
		if (!in_array($l, $this->collDosenMataKuliahs, true)) { // only add it if the **same** object is not already associated
			array_push($this->collDosenMataKuliahs, $l);
			$l->setMataKuliah($this);
		}
	}


