	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collJadwalUjians) {
				foreach ((array) $this->collJadwalUjians as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collKelasMKs) {
				foreach ((array) $this->collKelasMKs as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collMataKuliahJurusans) {
				foreach ((array) $this->collMataKuliahJurusans as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collTranskrips) {
				foreach ((array) $this->collTranskrips as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collTranskripAslis) {
				foreach ((array) $this->collTranskripAslis as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collMinatMataKuliahs) {
				foreach ((array) $this->collMinatMataKuliahs as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collDosenMataKuliahs) {
				foreach ((array) $this->collDosenMataKuliahs as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collJadwalUjians = null;
		$this->collKelasMKs = null;
		$this->collMataKuliahJurusans = null;
		$this->collTranskrips = null;
		$this->collTranskripAslis = null;
		$this->collMinatMataKuliahs = null;
		$this->collDosenMataKuliahs = null;
	}

	// symfony_behaviors behavior
	
	/**
	 * Calls methods defined via {@link sfMixer}.
	 */
	public function __call($method, $arguments)
	{
	  if (!$callable = sfMixer::getCallable('BaseMataKuliah:'.$method))
	  {
	    throw new sfException(sprintf('Call to undefined method BaseMataKuliah::%s', $method));
	  }
	
	  array_unshift($arguments, $this);
	
	  return call_user_func_array($callable, $arguments);
	}

