		KaryawanPeer::addSelectColumns($criteria);
		$startcol3 = $startcol2 + (KaryawanPeer::NUM_COLUMNS - KaryawanPeer::NUM_LAZY_LOAD_COLUMNS);

		$criteria->addJoin(KaryawanNonJagaPeer::KODE_KARYAWAN, KaryawanPeer::KODE_KARYAWAN, $join_behavior);

		// symfony_behaviors behavior
		foreach (sfMixer::getCallables(self::getMixerPreSelectHook(__FUNCTION__)) as $sf_hook)
		{
		  call_user_func($sf_hook, 'BaseKaryawanNonJagaPeer', $criteria, $con);
		}

		$stmt = BasePeer::doSelect($criteria, $con);
		$results = array();

		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$key1 = KaryawanNonJagaPeer::getPrimaryKeyHashFromRow($row, 0);
			if (null !== ($obj1 = KaryawanNonJagaPeer::getInstanceFromPool($key1))) {
				// We no longer rehydrate the object, since this can cause data loss.
				// See http://propel.phpdb.org/trac/ticket/509
				// $obj1->hydrate($row, 0, true); // rehydrate
			} else {
				$cls = KaryawanNonJagaPeer::getOMClass(false);

				$obj1 = new $cls();
				$obj1->hydrate($row);
				KaryawanNonJagaPeer::addInstanceToPool($obj1, $key1);
			} // if obj1 already loaded

			// Add objects for joined Karyawan rows

			$key2 = KaryawanPeer::getPrimaryKeyHashFromRow($row, $startcol2);
			if ($key2 !== null) {
				$obj2 = KaryawanPeer::getInstanceFromPool($key2);
				if (!$obj2) {

					$cls = KaryawanPeer::getOMClass(false);

					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol2);
					KaryawanPeer::addInstanceToPool($obj2, $key2);
				} // if obj2 loaded

				// Add the $obj1 (KaryawanNonJaga) to the collection in $obj2 (Karyawan)
				$obj1->setKaryawan($obj2);
			} // if joined row not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}

