	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(RuangBlockPeer::DATABASE_NAME);

		if ($this->isColumnModified(RuangBlockPeer::KODE_RUANG)) $criteria->add(RuangBlockPeer::KODE_RUANG, $this->kode_ruang);
		if ($this->isColumnModified(RuangBlockPeer::MULAI_MINGGU)) $criteria->add(RuangBlockPeer::MULAI_MINGGU, $this->mulai_minggu);
		if ($this->isColumnModified(RuangBlockPeer::MULAI_HARI)) $criteria->add(RuangBlockPeer::MULAI_HARI, $this->mulai_hari);
		if ($this->isColumnModified(RuangBlockPeer::SAMPAI_MINGGU)) $criteria->add(RuangBlockPeer::SAMPAI_MINGGU, $this->sampai_minggu);
		if ($this->isColumnModified(RuangBlockPeer::SAMPAI_HARI)) $criteria->add(RuangBlockPeer::SAMPAI_HARI, $this->sampai_hari);

		return $criteria;
	}

	/**
	 * Builds a Criteria object containing the primary key for this object.
	 *
	 * Unlike buildCriteria() this method includes the primary key values regardless
	 * of whether or not they have been modified.
	 *
	 * @return     Criteria The Criteria object containing value(s) for primary key(s).
	 */
	public function buildPkeyCriteria()
	{
		$criteria = new Criteria(RuangBlockPeer::DATABASE_NAME);

		$criteria->add(RuangBlockPeer::KODE_RUANG, $this->kode_ruang);

		return $criteria;
	}

	/**
	 * Returns the primary key for this object (row).
	 * @return     string
	 */
	public function getPrimaryKey()
	{
		return $this->getKodeRuang();
	}

	/**
	 * Generic method to set the primary key (kode_ruang column).
	 *
	 * @param      string $key Primary key.
	 * @return     void
	 */
	public function setPrimaryKey($key)
	{
		$this->setKodeRuang($key);
	}

	/**
	 * Sets contents of passed object to values from current object.
	 *
	 * If desired, this method can also make copies of all associated (fkey referrers)
	 * objects.
	 *
	 * @param      object $copyObj An object of RuangBlock (or compatible) type.
	 * @param      boolean $deepCopy Whether to also copy all rows that refer (by fkey) to the current row.
	 * @throws     PropelException
	 */
	public function copyInto($copyObj, $deepCopy = false)
	{

		$copyObj->setKodeRuang($this->kode_ruang);

		$copyObj->setMulaiMinggu($this->mulai_minggu);

		$copyObj->setMulaiHari($this->mulai_hari);

		$copyObj->setSampaiMinggu($this->sampai_minggu);

		$copyObj->setSampaiHari($this->sampai_hari);


		$copyObj->setNew(true);

	}

	/**
	 * Makes a copy of this object that will be inserted as a new row in table when saved.
	 * It creates a new object filling in the simple attributes, but skipping any primary
	 * keys that are defined for the table.
	 *
	 * If desired, this method can also make copies of all associated (fkey referrers)
	 * objects.
	 *
	 * @param      boolean $deepCopy Whether to also copy all rows that refer (by fkey) to the current row.
	 * @return     RuangBlock Clone of current object.
	 * @throws     PropelException
	 */
	public function copy($deepCopy = false)
	{
		// we use get_class(), because this might be a subclass
		$clazz = get_class($this);
		$copyObj = new $clazz();
		$this->copyInto($copyObj, $deepCopy);
		return $copyObj;
	}

	/**
	 * Returns a peer instance associated with this om.
	 *
	 * Since Peer classes are not to have any instance attributes, this method returns the
	 * same instance for all member of this class. The method could therefore
	 * be static, but this would prevent one from overriding the behavior.
	 *
	 * @return     RuangBlockPeer
	 */
	public function getPeer()
	{
		if (self::$peer === null) {
			self::$peer = new RuangBlockPeer();
		}
		return self::$peer;
	}

	/**
	 * Declares an association between this object and a Ruang object.
	 *
	 * @param      Ruang $v
	 * @return     RuangBlock The current object (for fluent API support)
	 * @throws     PropelException
	 */
	public function setRuang(Ruang $v = null)
	{
		if ($v === null) {
			$this->setKodeRuang(NULL);
		} else {
			$this->setKodeRuang($v->getKodeRuang());
		}

		$this->aRuang = $v;

		// Add binding for other direction of this 1:1 relationship.
		if ($v !== null) {
			$v->setRuangBlock($this);
		}

		return $this;
	}


	/**
	 * Get the associated Ruang object
	 *
	 * @param      PropelPDO Optional Connection object.
	 * @return     Ruang The associated Ruang object.
	 * @throws     PropelException
	 */
	public function getRuang(PropelPDO $con = null)
	{
		if ($this->aRuang === null && (($this->kode_ruang !== "" && $this->kode_ruang !== null))) {
			$this->aRuang = RuangPeer::retrieveByPk($this->kode_ruang);
			// Because this foreign key represents a one-to-one relationship, we will create a bi-directional association.
			$this->aRuang->setRuangBlock($this);
		}
		return $this->aRuang;
	}

	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
		} // if ($deep)

			$this->aRuang = null;
	}

	// symfony_behaviors behavior
	
	/**
	 * Calls methods defined via {@link sfMixer}.
	 */
	public function __call($method, $arguments)
	{
	  if (!$callable = sfMixer::getCallable('BaseRuangBlock:'.$method))
	  {
	    throw new sfException(sprintf('Call to undefined method BaseRuangBlock::%s', $method));
	  }
	
	  array_unshift($arguments, $this);
	
	  return call_user_func_array($callable, $arguments);
	}

} // BaseRuangBlock
